mapper.select:
		select *
        from (
        select rownum as rno, no, exhibitionname, exhibitionno, title, name, writeday, readcount, content
        from (
        select C.content, C.no, E.exhibitionname, E.exhibitionno, C.title, C.name, C.writeday, C.readcount
		from (
		select V.*, M.name
		from community V join member M 
		on V.fk_idx = M.idx
		) C  join exhibition E 
		on C.fk_exhibitionno = E.exhibitionno
        where 1= 1 
        
        	
        		and exhibitionname like '%' || #{searchWord} || '%'
        	
	        
		        and (title like '%' || #{searchWord} || '%'
		        or content like '%' || #{searchWord} || '%')
	        
	        
	       		and name like '%' || #{searchWord} || '%'
	        
        
        
        
		order by C.no desc
        ) T ) Z
        
        where rno between #{startRno} and #{endRno}

select exhibitionno, exhibitionname, author, galleryname from ( select * from (select * from exhibition
where exhibitionname like '%' || #{ value } || '%'
) V join exhibitionDetail D on V.exhibitionno = D.fk_exhibitionno ) E join gallery G on E.fk_galleryno = G.galleryno order by to_date(startdate) desc
